import React from "react";
import Drawer from "@mui/material/Drawer";
import DrawerHeader from "./DrawerHeader";
import Divider from "@mui/material/Divider";
import List from "@mui/material/List";

import MenuList from "../sidebar-list/MenuList";

const DrawerComponent = ({ open, handleDrawerClose, theme }) => {
  const drawerWidth = 240; // Define drawerWidth here

  return (
    <Drawer
      sx={{
        width: drawerWidth,
        flexShrink: 0,
        "& .MuiDrawer-paper": {
          width: drawerWidth,
          boxSizing: "border-box",
        },
      }}
      variant="persistent"
      anchor="left"
      open={open}
    >
      <DrawerHeader handleDrawerClose={handleDrawerClose} theme={theme} />
      <Divider />
      <List>
        {["Inbox", "Starred", "Send email", "Drafts"].map((text, index) => (
          <MenuList key={text} text={text} index={index} />
        ))}
      </List>
      <Divider />
      <List>
        {["All mail", "Trash", "Spam"].map((text, index) => (
          <MenuList key={text} text={text} index={index} />
        ))}
      </List>
    </Drawer>
  );
};

export default DrawerComponent;
