import React from "react";
import DataTable from "react-data-table-component";
import { useDispatch, useSelector } from "react-redux";
import { useNavigate } from "react-router-dom"; // Import useNavigate
import { editProduct, removeProduct } from "../../redux/action/ProductAction";
import { Button, Card } from "@mui/material";

const ProductList = () => {
  const products = useSelector((state) => state.products.products);
  console.log(products);
  const Navigate = useNavigate();
  const dispatch = useDispatch();
  console.log(products);

  const columns = [
    {
      name: "Name",
      selector: (row) => row.name, // Use a selector function
    },
    {
      name: "vendors",
      selector: (row) => row.vendors,
      cell: (row) => row.vendors.map((vendor) => vendor.nameV).join(", "),
    },
    {
      name: "variants",
      cell: (row) => {
        const variantStrings = row.vendors.map((vendor) =>
          vendor.variants
            .map((variant) => {
              const variantValues = Object.entries(variant).map(
                ([key, value]) => `${value}`
              );
              return variantValues.join(":");
            })
            .join(", ")
        );
        return variantStrings.join(", ");
      },
    },
    {
      name: "Actions",
      cell: (row) => {
        const handleEdit = (productId) => {
          dispatch(editProduct(productId));
          console.log(productId, "dispatch");
          console.log(productId);
          Navigate(`/edit/${productId}`);
        };

        const handleDelete = (id) => {
          dispatch(removeProduct(id));
        };

        return (
          <div>
            <Button onClick={() => handleEdit(row.id)}>Edit</Button>
            <Button onClick={() => handleDelete(row.id)}>Delete</Button>
          </div>
        );
      },
    },
  ];

  return (
    <div className="product_list">
      {products.map((product) => (
        <Card
          className="product_card"
          key={product.id}
          // onClick={() => Navigate(`/edit/${product.id}`)}
        >
          <h1>{product.name}</h1>
          <p>{product.description}</p>
          <p>
            Vendors: {product.vendors.map((vendor) => vendor.nameV).join(", ")}
          </p>
          <p>
            Variants:{" "}
            {product.vendors
              .map((vendor) =>
                vendor.variants
                  .map((variant) => {
                    const variantValues = Object.entries(variant).map(
                      ([key, value]) => `${value}`
                    );
                    return variantValues.join(":");
                  })
                  .join(", ")
              )
              .join(", ")}
          </p>
          <button onClick={() => Navigate(`/edit/${product.id}`)}>Edit</button>
          <button onClick={() => handleDelete(product.id)}>Delete</button>
        </Card>
      ))}
      {/* <DataTable
        title="Product Data"
        columns={columns}
        data={products}
        pagination
      /> */}
    </div>
  );
};

export default ProductList;
