import React from "react";
import { Drawer, List, ListItem, ListItemText, Collapse } from "@mui/material";
import { ExpandLess, ExpandMore } from "@mui/icons-material";
import "./Sidebar.css"; // Import the custom CSS

const Sidebar = () => {
  const [open, setOpen] = React.useState(false);

  const handleSubmenuClick = () => {
    setOpen(!open);
  };

  return (
    <Drawer
      variant="permanent"
      anchor="left"
      classes={{
        paper: "sidebar-paper", // Apply styles to the sidebar paper (background)
      }}
      className="sidebar" // Apply the sidebar styles
    >
      <List>
        <ListItem button component="button" onClick={handleSubmenuClick}>
          <ListItemText primary="Parent Menu" />
          {open ? <ExpandLess /> : <ExpandMore />}
        </ListItem>
        <Collapse in={open} timeout="auto" unmountOnExit>
          <List component="div" disablePadding className="submenu">
            <ListItem button component="button">
              <ListItemText primary="Product List" />
            </ListItem>
            <ListItem button component="button">
              <ListItemText primary="Create Product" />
            </ListItem>
          </List>
        </Collapse>
        <ListItem button component="button">
          <ListItemText primary="Another Menu" />
        </ListItem>
      </List>
    </Drawer>
  );
};

export default Sidebar;
