import React from "react";
import Card from "@mui/material/Card";
import { Formik, Form, Field, FieldArray, ErrorMessage } from "formik";
import * as Yup from "yup"; // Import Yup for validation
import { useDispatch, useSelector } from "react-redux";
import { useNavigate } from "react-router-dom"; // Import useNavigate
import { editProduct } from "../../redux/action/ProductAction";

const EditProduct = () => {
  const dispatch = useDispatch();
  const navigate = useNavigate(); // Access the navigate function
  const productId = useSelector((state) => state.products.editingProductId);

  const product = useSelector((state) =>
    state.products.products.find((product) => product.id === productId)
  );

  const validationSchema = Yup.object().shape({
    name: Yup.string().required("Name is required"),
    description: Yup.string(),
    vendors: Yup.array().of(
      Yup.object().shape({
        nameV: Yup.string().required("Vendor Name is required"),
        isMain: Yup.boolean(),
        variants: Yup.array().of(
          Yup.object().shape({
            variant: Yup.string(),
            number: Yup.string(),
          })
        ),
      })
    ),
  });

  return (
    <div className="edit-product">
      <Card className="variant-card">
        <Formik
          initialValues={product}
          validationSchema={validationSchema}
          onSubmit={(values) => {
            // Handle the form submission and dispatch your update action
            dispatch(editProduct(values));
            // After updating, navigate back to the product list
            navigate("/product");
          }}
        >
          {({ values, setFieldValue }) => (
            <Form>
              <div className="form-group">
                <label htmlFor="name">Name:</label>
                <Field type="text" name="name" className="form-control" />
                <ErrorMessage name="name" component="div" className="error" />
              </div>

              <div className="form-group">
                <label htmlFor="description">Description:</label>
                <Field
                  as="textarea"
                  name="description"
                  className="form-control"
                />
              </div>

              <FieldArray name="vendors">
                {({ push, remove }) => (
                  <div>
                    {values.vendors.map((vendor, vendorIndex) => (
                      <div key={vendorIndex} className="vendor-section">
                        <div className="form-group">
                          <label htmlFor={`vendors[${vendorIndex}].nameV`}>
                            Vendor Name:
                          </label>
                          <Field
                            type="text"
                            name={`vendors[${vendorIndex}].nameV`}
                            className="form-control"
                          />
                          <ErrorMessage
                            name={`vendors[${vendorIndex}].nameV`}
                            component="div"
                            className="error"
                          />
                        </div>

                        <div className="form-group">
                          <label htmlFor={`vendors[${vendorIndex}].isMain`}>
                            Is Main:
                          </label>
                          <Field
                            type="checkbox"
                            name={`vendors[${vendorIndex}].isMain`}
                          />
                        </div>

                        <FieldArray name={`vendors[${vendorIndex}].variants`}>
                          {({ push: pushVariant, remove: removeVariant }) => (
                            <div>
                              {vendor.variants.map((variant, variantIndex) => (
                                <div
                                  key={variantIndex}
                                  className="variant-section"
                                >
                                  <div className="form-group">
                                    <label
                                      htmlFor={`vendors[${vendorIndex}].variants[${variantIndex}].variant`}
                                    >
                                      Variant:
                                    </label>
                                    <Field
                                      as="select"
                                      name={`vendors[${vendorIndex}].variants[${variantIndex}].variant`}
                                      className="form-control"
                                    >
                                      <option value="">Select Variant</option>
                                      <option value="Option 1">Option 1</option>
                                      <option value="Option 2">Option 2</option>
                                    </Field>
                                    <ErrorMessage
                                      name={`vendors[${vendorIndex}].variants[${variantIndex}].variant`}
                                      component="div"
                                      className="error"
                                    />
                                  </div>

                                  <div className="form-group">
                                    <label
                                      htmlFor={`vendors[${vendorIndex}].variants[${variantIndex}].number`}
                                    >
                                      Number:
                                    </label>
                                    <Field
                                      as="select"
                                      name={`vendors[${vendorIndex}].variants[${variantIndex}].number`}
                                      className="form-control"
                                    >
                                      <option value="">Select Number</option>
                                      <option value="Number 1">Number 1</option>
                                      <option value="Number 2">Number 2</option>
                                    </Field>
                                    <ErrorMessage
                                      name={`vendors[${vendorIndex}].variants[${variantIndex}].number`}
                                      component="div"
                                      className="error"
                                    />
                                  </div>

                                  {variantIndex > 0 && (
                                    <button
                                      type="button"
                                      onClick={() =>
                                        removeVariant(variantIndex)
                                      }
                                    >
                                      Remove Variant
                                    </button>
                                  )}
                                </div>
                              ))}
                              <button
                                type="button"
                                onClick={() =>
                                  pushVariant({ variant: "", number: "" })
                                }
                              >
                                Add Variant
                              </button>
                            </div>
                          )}
                        </FieldArray>

                        {vendorIndex > 0 && (
                          <button
                            type="button"
                            onClick={() => remove(vendorIndex)}
                          >
                            Remove Vendor
                          </button>
                        )}
                      </div>
                    ))}
                    <button
                      type="button"
                      onClick={() =>
                        push({
                          nameV: "",
                          isMain: false,
                          variants: [{ variant: "", number: "" }],
                        })
                      }
                    >
                      Add Vendor
                    </button>
                  </div>
                )}
              </FieldArray>

              <button type="submit">Update</button>
            </Form>
          )}
        </Formik>
      </Card>
    </div>
  );
};

export default EditProduct;
