import { ActionTypes } from "../../constant/ActionTypes";
import { v4 as uuidv4 } from "uuid";

// reducers/productReducer.js
const initialState = {
  products: [
    {
      id: uuidv4(),
      name: "Product1",
      description: "This is a product",
      vendors: [
        {
          nameV: "Vendor1",
          is_main: true,
          variants: [{ xs: 20 }, { sm: 20 }, { l: 30 }, { xl: 35 }],
        },
      ],
    },
    {
      id: uuidv4(),
      name: "Product2",
      description: "This is a product",
      vendors: [
        {
          nameV: "Vendor2",
          is_main: true,
          variants: [{ xs: 20 }],
        },
        {
          nameV: "Vendor3",
          is_main: false,
          variants: [{ l: 30 }, { xl: 35 }],
        },
      ],
    },
  ],
  editingProductId: null,
};

const productReducer = (state = initialState, action) => {
  switch (action.type) {
    case ActionTypes.SET_PRODUCTS:
      let productAll = JSON.parse(localStorage.getItem("products"));
      localStorage.setItem(
        "products",
        JSON.stringify({ products: state.products })
      );
      state.products.product = productAll;

    case ActionTypes.ADD_PRODUCT:
      const id = uuidv4();
      let newProduct = { ...action.payload, id };

      let newProductData = [...state.products, { ...newProduct }];

      localStorage.setItem("products", JSON.stringify(newProductData));

    case ActionTypes.REMOVE_PRODUCT:
      return {
        ...state,
        products: state.products.filter(
          (product) => product.id !== action.payload
        ),
      };

    case ActionTypes.EDIT_PRODUCT:
      const data = state.products.map((product) => {
        return product.id === action.payload.id
          ? { ...action.payload }
          : product;
      });

      localStorage.setItem("products", JSON.stringify(data));

      state.products.products = data;
      return {
        ...state,
        editingProductId: action.payload,
      };

    default:
      return state;
  }
};

export default productReducer;
