import React from "react";
import Card from "@mui/material/Card";
import { Formik, Form, FieldArray } from "formik";
import * as Yup from "yup"; // Import Yup for validation
import { useDispatch, useSelector } from "react-redux";
import { useNavigate } from "react-router-dom"; // Import useNavigate
import { editProduct } from "../../redux/action/ProductAction";
import "../createproduct/CreateProduct.css";
import {
  Button,
  FormControl,
  FormControlLabel,
  Grid,
  InputLabel,
  MenuItem,
  Radio,
  Select,
  TextField,
} from "@mui/material";
import { toast } from "react-toastify";
const EditProduct = () => {
  const dispatch = useDispatch();
  const navigate = useNavigate(); // Access the navigate function
  const allProduct = useSelector((state) => state.products.products);
  const productId = useSelector((state) => state.products.editingProductId);

  const product = useSelector((state) =>
    state.products.products.find((product) => product.id === productId)
  );

  const validationSchema = Yup.object().shape({
    name: Yup.string().required("Name is required"),
    description: Yup.string(),
    vendors: Yup.array().of(
      Yup.object().shape({
        nameV: Yup.string().required("Vendor Name is required"),
        isMain: Yup.boolean(),
        variants: Yup.array().of(
          Yup.object().shape({
            variant: Yup.string(),
            number: Yup.string(),
          })
        ),
      })
    ),
  });

  return (
    <div className="create-product">
      <Card className="varient-card">
        <Formik
          initialValues={product}
          validationSchema={validationSchema}
          onSubmit={(values) => {
            const allReadyAxist = allProduct.some((product) => {
              return product.name === values.name;
            });

            if (!allReadyAxist) {
              dispatch(editProduct(values));
              toast("Product Updated Successfully");
              navigate("/");
            } else {
              toast("Product Already Exist");
            }

            // Handle the form submission and dispatch your update action
            // After updating, navigate back to the product list
          }}
        >
          {({
            values,
            setFieldValue,
            errors,
            touched,
            handleBlur,
            handleChange,
            handleReset,
          }) => (
            <Form>
              <Grid container spacing={2}>
                <Grid item xs={6}>
                  <TextField
                    id="name"
                    name="name"
                    label="Name"
                    variant="outlined"
                    fullWidth
                    value={values.name}
                    onChange={handleChange}
                    onBlur={handleBlur}
                    errors={touched.name && Boolean(errors.name)}
                    helperText={touched.name && errors.name}
                  />
                </Grid>
                <Grid item xs={6}>
                  <TextField
                    id="description"
                    name="description"
                    label="description"
                    variant="outlined"
                    multiline
                    fullWidth
                    value={values.description}
                    onChange={handleChange}
                    onBlur={handleBlur}
                    errors={touched.description && Boolean(errors.description)}
                    helperText={touched.description && errors.description}
                  />
                </Grid>
              </Grid>
              <FieldArray name="vendors">
                {({ push, remove }) => (
                  <div>
                    {values.vendors.map((vendor, vendorIndex) => (
                      <div key={vendorIndex} className="vendor-section">
                        <Grid container spacing={2}>
                          <Grid item xs={10}>
                            <TextField
                              label="Vendor Name"
                              name={`vendors.${vendorIndex}.nameV`}
                              variant="outlined"
                              fullWidth
                              value={values.vendors[vendorIndex].nameV}
                              onChange={handleChange}
                              onBlur={handleBlur}
                              error={
                                touched.vendors &&
                                touched.vendors[vendorIndex] &&
                                Boolean(
                                  errors.vendors &&
                                    errors.vendors[vendorIndex] &&
                                    errors.vendors[vendorIndex].nameV
                                )
                              }
                              helperText={
                                touched.vendors &&
                                touched.vendors[vendorIndex] &&
                                errors.vendors &&
                                errors.vendors[vendorIndex] &&
                                errors.vendors[vendorIndex].nameV
                              }
                            />
                          </Grid>
                          <Grid item xs={2}>
                            <FormControlLabel
                              control={
                                <Radio
                                  checked={vendor.isMain}
                                  onChange={(e) => {
                                    // Ensure only one vendor can be marked as main
                                    if (e.target.checked) {
                                      setFieldValue(
                                        `vendors.${vendorIndex}.isMain`,
                                        true
                                      );
                                      values.vendors.forEach((v, index) => {
                                        if (index !== vendorIndex) {
                                          setFieldValue(
                                            `vendors.${index}.isMain`,
                                            false
                                          );
                                        }
                                      });
                                    } else {
                                      setFieldValue(
                                        `vendors.${vendorIndex}.isMain`,
                                        false
                                      );
                                    }
                                  }}
                                />
                              }
                              label="Is Main"
                            />
                          </Grid>
                        </Grid>
                        <FieldArray name={`vendors.${vendorIndex}.variants`}>
                          {({ push: pushVariant, remove: removeVariant }) => (
                            <div>
                              {vendor.variants.map((variant, variantIndex) => (
                                <div
                                  key={variantIndex}
                                  className="variant-section"
                                >
                                  <Grid container spacing={2}>
                                    <Grid item xs={6}>
                                      <FormControl fullWidth variant="outlined">
                                        <InputLabel
                                          id={`vendors.${vendorIndex}.variants.${variantIndex}.variant`}
                                        >
                                          Variant
                                        </InputLabel>
                                        <Select
                                          labelId={`vendors.${vendorIndex}.variants.${variantIndex}.variant`}
                                          label="Variant"
                                          name={`vendors.${vendorIndex}.variants.${variantIndex}.variant`}
                                          value={
                                            values.vendors[vendorIndex]
                                              .variants[variantIndex].variant
                                          }
                                          onChange={handleChange}
                                          onBlur={handleBlur}
                                        >
                                          <MenuItem value="">
                                            Select Variant
                                          </MenuItem>
                                          <MenuItem value="xs">xs</MenuItem>
                                          <MenuItem value="sm">sm</MenuItem>
                                          <MenuItem value="l">l</MenuItem>
                                          <MenuItem value="xl">xl</MenuItem>
                                        </Select>
                                      </FormControl>
                                      {touched.vendors &&
                                        touched.vendors[vendorIndex] &&
                                        touched.vendors[vendorIndex].variants &&
                                        touched.vendors[vendorIndex].variants[
                                          variantIndex
                                        ] &&
                                        errors.vendors &&
                                        errors.vendors[vendorIndex] &&
                                        errors.vendors[vendorIndex].variants &&
                                        errors.vendors[vendorIndex].variants[
                                          variantIndex
                                        ] &&
                                        errors.vendors[vendorIndex].variants[
                                          variantIndex
                                        ].variant && (
                                          <div className="error">
                                            {
                                              errors.vendors[vendorIndex]
                                                .variants[variantIndex].variant
                                            }
                                          </div>
                                        )}
                                    </Grid>
                                    <Grid item xs={6}>
                                      <div className="form-group">
                                        <FormControl
                                          fullWidth
                                          variant="outlined"
                                        >
                                          <InputLabel
                                            id={`vendors.${vendorIndex}.variants.${variantIndex}.number`}
                                          >
                                            Number
                                          </InputLabel>
                                          <Select
                                            labelId={`vendors.${vendorIndex}.variants.${variantIndex}.number`}
                                            label="Number"
                                            name={`vendors.${vendorIndex}.variants.${variantIndex}.number`}
                                            value={
                                              values.vendors[vendorIndex]
                                                .variants[variantIndex].number
                                            }
                                            onChange={handleChange}
                                            onBlur={handleBlur}
                                          >
                                            <MenuItem value="">
                                              Select Number
                                            </MenuItem>
                                            <MenuItem value="20">20</MenuItem>
                                            <MenuItem value="30">30</MenuItem>
                                            <MenuItem value="25">25</MenuItem>
                                            <MenuItem value="35">35</MenuItem>
                                            {/* Add your number options here */}
                                          </Select>
                                        </FormControl>
                                        {touched.vendors &&
                                          touched.vendors[vendorIndex] &&
                                          touched.vendors[vendorIndex]
                                            .variants &&
                                          touched.vendors[vendorIndex].variants[
                                            variantIndex
                                          ] &&
                                          errors.vendors &&
                                          errors.vendors[vendorIndex] &&
                                          errors.vendors[vendorIndex]
                                            .variants &&
                                          errors.vendors[vendorIndex].variants[
                                            variantIndex
                                          ] &&
                                          errors.vendors[vendorIndex].variants[
                                            variantIndex
                                          ].number && (
                                            <div className="error">
                                              {
                                                errors.vendors[vendorIndex]
                                                  .variants[variantIndex].number
                                              }
                                            </div>
                                          )}
                                      </div>
                                    </Grid>
                                  </Grid>
                                  {variantIndex > 0 && (
                                    <Button
                                      variant="outlined"
                                      color="error"
                                      onClick={() =>
                                        removeVariant(variantIndex)
                                      }
                                      className="remove-button"
                                    >
                                      Remove Variant
                                    </Button>
                                  )}
                                </div>
                              ))}
                              <Button
                                variant="contained"
                                onClick={() =>
                                  pushVariant({ variant: "", number: "" })
                                }
                                className="add-button"
                              >
                                Add Variant
                              </Button>
                            </div>
                          )}
                        </FieldArray>
                        {vendorIndex > 0 && (
                          <Button
                            variant="outlined"
                            color="error"
                            onClick={() => remove(vendorIndex)}
                            className="remove-button"
                          >
                            Remove Vendor
                          </Button>
                        )}
                      </div>
                    ))}
                    <Button
                      variant="contained"
                      onClick={() =>
                        push({
                          vendorName: "",
                          isMain: false,
                          variants: [{ variant: "", number: "" }],
                        })
                      }
                      className="add-button"
                    >
                      Add Vendor
                    </Button>
                  </div>
                )}
              </FieldArray>
              <Grid container spacing={2}>
                <Grid item xs={6} className="text-right">
                  <Button
                    variant="contained"
                    className="submit-button"
                    onClick={() => navigate("/")}
                  >
                    Cancle
                  </Button>
                </Grid>
                <Grid item xs={6}>
                  <Button
                    type="submit"
                    variant="contained"
                    className="submit-button"
                  >
                    Submit
                  </Button>
                </Grid>
              </Grid>
            </Form>
          )}
        </Formik>
      </Card>
    </div>
  );
};

export default EditProduct;
