import React from "react";
import { Drawer, List, ListItem, ListItemText, Collapse } from "@mui/material";
import makeStyles from "@mui/styles";
import { ExpandLess, ExpandMore } from "@mui/icons-material";

const useStyles = makeStyles({
  sidebar: {
    width: "240px",
  },
  sidebarPaper: {
    width: "240px",
    backgroundColor: "#333", // Set your background color
    color: "#fff", // Set your text color
  },
  submenu: {
    paddingLeft: "20px", // Indent child menus
  },
  submenuText: {
    color: "#fff", // Set child menu text color
  },
});

const Sidebar = () => {
  const classes = useStyles();
  const [open, setOpen] = React.useState(false);

  const handleSubmenuClick = () => {
    setOpen(!open);
  };

  return (
    <Drawer
      variant="permanent"
      anchor="left"
      classes={{
        paper: classes.sidebarPaper,
      }}
      className={classes.sidebar}
    >
      <List>
        <ListItem button onClick={handleSubmenuClick}>
          <ListItemText primary="Parent Menu" />
          {open ? <ExpandLess /> : <ExpandMore />}
        </ListItem>
        <Collapse in={open} timeout="auto" unmountOnExit>
          <List component="div" disablePadding className={classes.submenu}>
            <ListItem button>
              <ListItemText
                primary="Child Menu 1"
                className={classes.submenuText}
              />
            </ListItem>
            <ListItem button>
              <ListItemText
                primary="Child Menu 2"
                className={classes.submenuText}
              />
            </ListItem>
          </List>
        </Collapse>
        <ListItem button>
          <ListItemText primary="Another Menu" />
        </ListItem>
      </List>
    </Drawer>
  );
};

export default Sidebar;
