import React from "react";
import DataTable from "react-data-table-component";
import { useDispatch, useSelector } from "react-redux";
import { useNavigate } from "react-router-dom"; // Import useNavigate
import { editProduct, removeProduct } from "../../redux/action/ProductAction";
import { Button } from "@mui/material";

const ProductList = () => {
  const products = useSelector((state) => state.products.products);
  console.log(products);
  const Navigate = useNavigate();
  const dispatch = useDispatch();
  console.log(products);

  const columns = [
    {
      name: "Name",
      selector: (row) => row.name, // Use a selector function
    },
    {
      name: "vendors",
      selector: (row) => row.vendors,
      cell: (row) => row.vendors.map((vendor) => vendor.nameV).join(", "),
    },
    {
      name: "variants",
      cell: (row) => {
        const variantStrings = row.vendors
          .map((vendor) => {
            const variants = vendor.variants[0];
            const variantStringsForVendor = Object.entries(variants)
              .map(([key, value]) => `${key}: ${value}`)
              .join(", ");
            return `${vendor.nameV}: ${variantStringsForVendor}`;
          })
          .join("\n");
        return variantStrings;
      },
    },
    {
      name: "Actions",
      cell: (row) => {
        const handleEdit = (productId) => {
          dispatch(editProduct(productId));
          console.log(productId, "dispatch");
          console.log(productId);
          Navigate(`/edit/${productId}`);
        };

        const handleDelete = (id) => {
          dispatch(removeProduct(id));
        };

        return (
          <div>
            <Button onClick={() => handleEdit(row.id)}>Edit</Button>
            <Button onClick={() => handleDelete(row.id)}>Delete</Button>
          </div>
        );
      },
    },
  ];

  return (
    <div>
      <DataTable
        title="Product Data"
        columns={columns}
        data={products}
        pagination
      />
    </div>
  );
};

export default ProductList;
