import React from "react";

import Card from "@mui/material/Card";
import "./CreateProduct.css";

import { Formik, Field, FieldArray, Form, ErrorMessage } from "formik";
import ProductSchema from "../../component/schema/ProductSchema";
import { Box, Grid } from "@mui/material";

// const Item = styled(Paper)(({ theme }) => ({
//   backgroundColor: theme.palette.mode === "dark" ? "#1A2027" : "#fff",
//   ...theme.typography.body2,
//   padding: theme.spacing(1),
//   textAlign: "center",
//   color: theme.palette.text.secondary,
// }));

const CreateProduct = () => {
  const handleFormSubmit = (values) => {
    // You can dispatch an action to save the data to Redux here.
    console.log(values);
  };

  return (
    <div className="create-product">
      <Card className="varient-card">
        <Formik
          initialValues={{
            name: "",
            description: "",
            vendors: [
              {
                vendorName: "",
                isMain: false,
                variants: [{ variant: "", number: "" }],
              },
            ],
          }}
          validationSchema={ProductSchema}
          onSubmit={handleFormSubmit}
        >
          {({ values }) => (
            <Form>
              <Box sx={{ flexGrow: 1 }}>
                <Grid container spacing={2}>
                  <Grid item xs={8}>
                    <div className="form-group">
                      <label htmlFor="name">Name:</label>
                      <Field
                        type="text"
                        id="name"
                        name="name"
                        className="form-control"
                      />
                      <ErrorMessage
                        name="name"
                        component="div"
                        className="error"
                      />
                    </div>
                  </Grid>
                  <Grid item xs={8}>
                    <div className="form-group">
                      <label htmlFor="description">Description:</label>
                      <Field
                        as="textarea"
                        id="description"
                        name="description"
                        className="form-control"
                      />
                    </div>
                  </Grid>
                </Grid>
              </Box>

              <FieldArray name="vendors">
                {({ push, remove }) => (
                  <div>
                    {values.vendors.map((vendor, vendorIndex) => (
                      <div key={vendorIndex} className="vendor-section">
                        <h3>Vendor #{vendorIndex + 1}</h3>
                        <div className="form-group">
                          <label htmlFor={`vendors.${vendorIndex}.vendorName`}>
                            Vendor Name:
                          </label>
                          <Field
                            type="text"
                            name={`vendors.${vendorIndex}.vendorName`}
                            className="form-control"
                          />
                          <ErrorMessage
                            name={`vendors.${vendorIndex}.vendorName`}
                            component="div"
                            className="error"
                          />
                        </div>

                        <div className="form-group">
                          <label htmlFor={`vendors.${vendorIndex}.isMain`}>
                            Is Main:
                          </label>
                          <Field
                            type="radio"
                            name={`vendors.${vendorIndex}.isMain`}
                            value="true"
                          />{" "}
                          Yes
                          <Field
                            type="radio"
                            name={`vendors.${vendorIndex}.isMain`}
                            value="false"
                          />{" "}
                          No
                        </div>

                        <FieldArray name={`vendors.${vendorIndex}.variants`}>
                          {({ push: pushVariant, remove: removeVariant }) => (
                            <div>
                              {vendor.variants.map((variant, variantIndex) => (
                                <div
                                  key={variantIndex}
                                  className="variant-section"
                                >
                                  <h4>Variant #{variantIndex + 1}</h4>
                                  <div className="form-group">
                                    <label
                                      htmlFor={`vendors.${vendorIndex}.variants.${variantIndex}.variant`}
                                    >
                                      Variant:
                                    </label>
                                    <Field
                                      type="text"
                                      name={`vendors.${vendorIndex}.variants.${variantIndex}.variant`}
                                      className="form-control"
                                    />
                                    <ErrorMessage
                                      name={`vendors.${vendorIndex}.variants.${variantIndex}.variant`}
                                      component="div"
                                      className="error"
                                    />
                                  </div>
                                  <div className="form-group">
                                    <label
                                      htmlFor={`vendors.${vendorIndex}.variants.${variantIndex}.number`}
                                    >
                                      Number:
                                    </label>
                                    <Field
                                      type="text"
                                      name={`vendors.${vendorIndex}.variants.${variantIndex}.number`}
                                      className="form-control"
                                    />
                                    <ErrorMessage
                                      name={`vendors.${vendorIndex}.variants.${variantIndex}.number`}
                                      component="div"
                                      className="error"
                                    />
                                  </div>
                                  {variantIndex > 0 && (
                                    <button
                                      type="button"
                                      onClick={() =>
                                        removeVariant(variantIndex)
                                      }
                                      className="remove-button"
                                    >
                                      Remove Variant
                                    </button>
                                  )}
                                </div>
                              ))}
                              <button
                                type="button"
                                onClick={() =>
                                  pushVariant({ variant: "", number: "" })
                                }
                                className="add-button"
                              >
                                Add Variant
                              </button>
                            </div>
                          )}
                        </FieldArray>
                        {vendorIndex > 0 && (
                          <button
                            type="button"
                            onClick={() => remove(vendorIndex)}
                            className="remove-button"
                          >
                            Remove Vendor
                          </button>
                        )}
                      </div>
                    ))}
                    <button
                      type="button"
                      onClick={() =>
                        push({
                          vendorName: "",
                          isMain: false,
                          variants: [{ variant: "", number: "" }],
                        })
                      }
                      className="add-button"
                    >
                      Add Vendor
                    </button>
                  </div>
                )}
              </FieldArray>

              <button type="submit" className="submit-button">
                Submit
              </button>
            </Form>
          )}
        </Formik>
      </Card>
    </div>
  );
};

export default CreateProduct;
