import React from "react";
import {
  Drawer,
  List,
  ListItem,
  ListItemText,
  Collapse,
  styled,
} from "@mui/material";
import { ExpandLess, ExpandMore } from "@mui/icons-material";

const SidebarWrapper = styled(Drawer)(({ theme }) => ({
  width: 240,
  flexShrink: 0,
}));

const SidebarList = styled(List)(({ theme }) => ({
  width: 240,
}));

const SidebarListItem = styled(ListItem)(({ theme }) => ({
  "&.Mui-selected": {
    backgroundColor: theme.palette.primary.main,
  },
}));

const SidebarListItemText = styled(ListItemText)(({ theme }) => ({
  color: theme.palette.primary.contrastText,
}));

const Sidebar = () => {
  const [open, setOpen] = React.useState(false);

  const handleSubmenuClick = () => {
    setOpen(!open);
  };

  return (
    <SidebarWrapper variant="permanent" anchor="left">
      <SidebarList>
        <SidebarListItem button onClick={handleSubmenuClick}>
          <SidebarListItemText primary="Parent Menu" />
          {open ? <ExpandLess /> : <ExpandMore />}
        </SidebarListItem>
        <Collapse in={open} timeout="auto" unmountOnExit>
          <List component="div" disablePadding>
            <SidebarListItem button>
              <SidebarListItemText primary="Child Menu 1" />
            </SidebarListItem>
            <SidebarListItem button>
              <SidebarListItemText primary="Child Menu 2" />
            </SidebarListItem>
          </List>
        </Collapse>
        <SidebarListItem button>
          <SidebarListItemText primary="Another Menu" />
        </SidebarListItem>
      </SidebarList>
    </SidebarWrapper>
  );
};

export default Sidebar;
