import React from "react";
import Card from "@mui/material/Card";
import "./CreateProduct.css";
import { Formik, Field, FieldArray, Form, ErrorMessage } from "formik";
import ProductSchema from "../../component/schema/ProductSchema";
import { useDispatch } from "react-redux";
import { updateProduct } from "../../redux/actions/productActions";
import { useHref } from "react-router-dom";

const EditProduct = ({ product }) => {
  const dispatch = useDispatch();
  const history = useHref();

  return (
    <div className="edit-product">
      <Card className="variant-card">
        <Formik
          initialValues={product} // Use the product data as initialValues
          validationSchema={ProductSchema}
          onSubmit={(values, actions) => {
            dispatch(updateProduct(values));
            history.push("/product");
          }}
        >
          {({ values, setFieldValue }) => (
            <Form>
              <div className="form-group">
                <label htmlFor="name">Name:</label>
                <Field
                  type="text"
                  id="name"
                  name="name"
                  className="form-control"
                />
                <ErrorMessage name="name" component="div" className="error" />
              </div>

              <div className="form-group">
                <label htmlFor="description">Description:</label>
                <Field
                  as="textarea"
                  id="description"
                  name="description"
                  className="form-control"
                />
              </div>

              <FieldArray name="vendors">
                {({ push, remove }) => (
                  <div>
                    {values.vendors.map((vendor, vendorIndex) => (
                      <div key={vendorIndex} className="vendor-section">
                        <h3>Vendor #{vendorIndex + 1}</h3>
                        <div className="form-group">
                          <label htmlFor={`vendors.${vendorIndex}.nameV`}>
                            Vendor Name:
                          </label>
                          <Field
                            type="text"
                            name={`vendors.${vendorIndex}.nameV`}
                            className="form-control"
                          />
                          <ErrorMessage
                            name={`vendors.${vendorIndex}.nameV`}
                            component="div"
                            className="error"
                          />
                        </div>

                        <div className="form-group">
                          <label htmlFor={`vendors.${vendorIndex}.isMain`}>
                            Is Main:
                          </label>
                          <Field
                            type="checkbox"
                            name={`vendors.${vendorIndex}.isMain`}
                            checked={vendor.isMain}
                            onChange={(e) => {
                              // Ensure only one vendor can be marked as main
                              if (e.target.checked) {
                                setFieldValue(
                                  `vendors.${vendorIndex}.isMain`,
                                  true
                                );
                                values.vendors.forEach((v, index) => {
                                  if (index !== vendorIndex) {
                                    setFieldValue(
                                      `vendors.${index}.isMain`,
                                      false
                                    );
                                  }
                                });
                              } else {
                                // Allow unchecking
                                setFieldValue(
                                  `vendors.${vendorIndex}.isMain`,
                                  false
                                );
                              }
                            }}
                          />
                        </div>

                        <FieldArray name={`vendors.${vendorIndex}.variants`}>
                          {({ push: pushVariant, remove: removeVariant }) => (
                            <div>
                              {vendor.variants.map((variant, variantIndex) => (
                                <div
                                  key={variantIndex}
                                  className="variant-section"
                                >
                                  <h4>Variant #{variantIndex + 1}</h4>
                                  <div className="form-group">
                                    <label
                                      htmlFor={`vendors.${vendorIndex}.variants.${variantIndex}.variant`}
                                    >
                                      Variant:
                                    </label>
                                    <Field
                                      as="select"
                                      name={`vendors.${vendorIndex}.variants.${variantIndex}.variant`}
                                      className="form-control"
                                    >
                                      <option value="">Select Variant</option>
                                      <option value="Option 1">Option 1</option>
                                      <option value="Option 2">Option 2</option>
                                      {/* Add your variant options here */}
                                    </Field>
                                    <ErrorMessage
                                      name={`vendors.${vendorIndex}.variants.${variantIndex}.variant`}
                                      component="div"
                                      className="error"
                                    />
                                  </div>
                                  <div className="form-group">
                                    <label
                                      htmlFor={`vendors.${vendorIndex}.variants.${variantIndex}.number`}
                                    >
                                      Number:
                                    </label>
                                    <Field
                                      as="select"
                                      name={`vendors.${vendorIndex}.variants.${variantIndex}.number`}
                                      className="form-control"
                                    >
                                      <option value="">Select Number</option>
                                      <option value="Number 1">Number 1</option>
                                      <option value="Number 2">Number 2</option>
                                      {/* Add your number options here */}
                                    </Field>
                                    <ErrorMessage
                                      name={`vendors.${vendorIndex}.variants.${variantIndex}.number`}
                                      component="div"
                                      className="error"
                                    />
                                  </div>
                                  {variantIndex > 0 && (
                                    <button
                                      type="button"
                                      onClick={() =>
                                        removeVariant(variantIndex)
                                      }
                                      className="remove-button"
                                    >
                                      Remove Variant
                                    </button>
                                  )}
                                </div>
                              ))}
                              <button
                                type="button"
                                onClick={() =>
                                  pushVariant({ variant: "", number: "" })
                                }
                                className="add-button"
                              >
                                Add Variant
                              </button>
                            </div>
                          )}
                        </FieldArray>
                        {vendorIndex > 0 && (
                          <button
                            type="button"
                            onClick={() => remove(vendorIndex)}
                            className="remove-button"
                          >
                            Remove Vendor
                          </button>
                        )}
                      </div>
                    ))}
                    <button
                      type="button"
                      onClick={() =>
                        push({
                          nameV: "",
                          isMain: false,
                          variants: [{ variant: "", number: "" }],
                        })
                      }
                      className="add-button"
                    >
                      Add Vendor
                    </button>
                  </div>
                )}
              </FieldArray>

              <button type="submit" className="submit-button">
                Update
              </button>
            </Form>
          )}
        </Formik>
      </Card>
    </div>
  );
};

export default EditProduct;
