import React from "react";
import Card from "@mui/material/Card";
import { Formik, Form, Field, FieldArray, ErrorMessage } from "formik";
import * as Yup from "yup"; // Import Yup for validation
import { useDispatch, useSelector } from "react-redux";
import { useNavigate } from "react-router-dom"; // Import useNavigate
import { editProduct } from "../../redux/action/ProductAction";
import "../createproduct/CreateProduct.css";

const EditProduct = () => {
  const dispatch = useDispatch();
  const navigate = useNavigate(); // Access the navigate function
  const productId = useSelector((state) => state.products.editingProductId);

  const product = useSelector((state) =>
    state.products.products.find((product) => product.id === productId)
  );

  const validationSchema = Yup.object().shape({
    name: Yup.string().required("Name is required"),
    description: Yup.string(),
    vendors: Yup.array().of(
      Yup.object().shape({
        nameV: Yup.string().required("Vendor Name is required"),
        isMain: Yup.boolean(),
        variants: Yup.array().of(
          Yup.object().shape({
            variant: Yup.string(),
            number: Yup.string(),
          })
        ),
      })
    ),
  });

  return (
    <div className="create-product">
      <Card className="varient-card">
        <Formik
          initialValues={product}
          validationSchema={validationSchema}
          onSubmit={(values) => {
            // Handle the form submission and dispatch your update action
            dispatch(editProduct(values));
            // After updating, navigate back to the product list
            navigate("/");
          }}
        >
          {({ values, setFieldValue }) => (
            <Form>
              <Grid container spacing={2}>
                <Grid item xs={6}>
                  <div className="form-group">
                    <TextField
                      type="text"
                      id="name"
                      name="name"
                      label="Name"
                      variant="outlined"
                      className="form-control"
                    />
                    <ErrorMessage
                      name="name"
                      component="div"
                      className="error"
                    />
                  </div>
                </Grid>
                <Grid item xs={6}>
                  <div className="form-group">
                    <TextField
                      label="description"
                      id="description"
                      name="description"
                      multiline
                      className="form-control"
                    />
                  </div>
                </Grid>
              </Grid>
              <FieldArray name="vendors">
                {({ push, remove }) => (
                  <div>
                    {values.vendors.map((vendor, vendorIndex) => (
                      <div key={vendorIndex} className="vendor-section">
                        <Grid container spacing={2} className="align-center">
                          <Grid item xs={10}>
                            <div className="form-group">
                              <TextField
                                label="Vendor Name"
                                type="text"
                                name={`vendors.${vendorIndex}.nameV`}
                                className="form-control"
                              />
                              <ErrorMessage
                                name={`vendors.${vendorIndex}.nameV`}
                                component="div"
                                className="error"
                              />
                            </div>
                          </Grid>
                          <Grid item xs={2}>
                            <div className="form-group">
                              <Field
                                type="radio"
                                name={`vendors.${vendorIndex}.isMain`}
                                checked={vendor.isMain}
                                onChange={(e) => {
                                  // Ensure only one vendor can be marked as main
                                  if (e.target.checked) {
                                    setFieldValue(
                                      `vendors.${vendorIndex}.isMain`,
                                      true
                                    );
                                    values.vendors.forEach((v, index) => {
                                      if (index !== vendorIndex) {
                                        setFieldValue(
                                          `vendors.${index}.isMain`,
                                          false
                                        );
                                      }
                                    });
                                  } else {
                                    // Allow unchecking
                                    setFieldValue(
                                      `vendors.${vendorIndex}.isMain`,
                                      false
                                    );
                                  }
                                }}
                              />
                              <label htmlFor={`vendors.${vendorIndex}.isMain`}>
                                Is Main:
                              </label>
                            </div>
                          </Grid>
                        </Grid>
                        <FieldArray name={`vendors.${vendorIndex}.variants`}>
                          {({ push: pushVariant, remove: removeVariant }) => (
                            <div>
                              {vendor.variants.map((variant, variantIndex) => (
                                <div
                                  key={variantIndex}
                                  className="variant-section"
                                >
                                  <Grid
                                    container
                                    spacing={2}
                                    className="align-center"
                                  >
                                    <Grid item xs={6}>
                                      <FormControl fullWidth>
                                        <InputLabel
                                          id={`vendors.${vendorIndex}.variants.${variantIndex}.variant`}
                                        >
                                          Variant
                                        </InputLabel>
                                        <Select
                                          labelId={`vendors.${vendorIndex}.variants.${variantIndex}.variant`}
                                          id={`vendors.${vendorIndex}.variants.${variantIndex}.variant`}
                                          name={`vendors.${vendorIndex}.variants.${variantIndex}.variant`}
                                        >
                                          <MenuItem value="xs">xs</MenuItem>
                                          <MenuItem value="sm">sm</MenuItem>
                                          <MenuItem value="l">l</MenuItem>
                                          <MenuItem value="xl">xl</MenuItem>
                                        </Select>
                                      </FormControl>
                                    </Grid>
                                    <Grid item xs={6}>
                                      <FormControl fullWidth>
                                        <InputLabel
                                          id={`vendors.${vendorIndex}.variants.${variantIndex}.number`}
                                        >
                                          Number
                                        </InputLabel>
                                        <Select
                                          labelId={`vendors.${vendorIndex}.variants.${variantIndex}.number`}
                                          id={`vendors.${vendorIndex}.variants.${variantIndex}.number`}
                                          name={`vendors.${vendorIndex}.variants.${variantIndex}.number`}
                                        >
                                          <MenuItem value="20">20</MenuItem>
                                          <MenuItem value="25">25</MenuItem>
                                          <MenuItem value="30">30</MenuItem>
                                          <MenuItem value="40">40</MenuItem>
                                        </Select>
                                      </FormControl>
                                    </Grid>
                                  </Grid>
                                  {variantIndex > 0 && (
                                    <Button
                                      variant="outlined"
                                      color="error"
                                      onClick={() =>
                                        removeVariant(variantIndex)
                                      }
                                      className="remove-button"
                                    >
                                      Remove Variant
                                    </Button>
                                  )}
                                </div>
                              ))}
                              <Button
                                variant="contained"
                                onClick={() =>
                                  pushVariant({ variant: "", number: "" })
                                }
                                className="add-button"
                              >
                                Add Variant
                              </Button>
                            </div>
                          )}
                        </FieldArray>
                        {vendorIndex > 0 && (
                          <Button
                            variant="outlined"
                            color="error"
                            onClick={() => remove(vendorIndex)}
                            className="remove-button"
                          >
                            Remove Vendor
                          </Button>
                        )}
                      </div>
                    ))}
                    <Button
                      type="button"
                      variant="contained"
                      onClick={() =>
                        push({
                          vendorName: "",
                          isMain: false,
                          variants: [{ variant: "", number: "" }],
                        })
                      }
                      className="add-button"
                    >
                      Add Vendor
                    </Button>
                  </div>
                )}
              </FieldArray>

              <button type="submit" className="submit-button">
                Submit
              </button>
            </Form>
          )}
        </Formik>
      </Card>
    </div>
  );
};

export default EditProduct;
