import React from "react";

import Card from "@mui/material/Card";
import "./CreateProduct.css";

import { Formik, Field, FieldArray, Form, ErrorMessage } from "formik";
import ProductSchema from "../../component/schema/ProductSchema";

import { useDispatch } from "react-redux";

import { addProduct } from "../../redux/action/ProductAction";
import { useNavigate } from "react-router-dom";
import {
  Button,
  FormControl,
  Grid,
  InputLabel,
  MenuItem,
  Select,
  TextField,
} from "@mui/material";
const CreateProduct = () => {
  const dispatch = useDispatch();
  const navigate = useNavigate();
  return (
    <div className="create-product">
      <Card className="varient-card">
        <Formik
          initialValues={{
            name: "",
            description: "",
            vendors: [
              {
                nameV: "",
                isMain: true,
                variants: [{ variant: "", number: "" }],
              },
            ],
          }}
          validationSchema={ProductSchema}
          onSubmit={(values, actions) => {
            console.log(values, "values");
            dispatch(addProduct(values));
            navigate("/");
            actions.resetForm();
          }}
        >
          {({ values, setFieldValue }) => (
            <Form>
              <Grid container spacing={2}>
                <Grid item xs={6}>
                  <div className="form-group">
                    <TextField
                      type="text"
                      id="name"
                      name="name"
                      label="Name"
                      variant="outlined"
                      className="form-control"
                    />
                    <ErrorMessage
                      name="name"
                      component="div"
                      className="error"
                    />
                  </div>
                </Grid>
                <Grid item xs={6}>
                  <div className="form-group">
                    <TextField
                      label="description"
                      id="description"
                      name="description"
                      multiline
                      className="form-control"
                    />
                  </div>
                </Grid>
              </Grid>
              <FieldArray name="vendors">
                {({ push, remove }) => (
                  <div>
                    {values.vendors.map((vendor, vendorIndex) => (
                      <div key={vendorIndex} className="vendor-section">
                        <Grid container spacing={2} className="align-center">
                          <Grid item xs={10}>
                            <div className="form-group">
                              <TextField
                                label="Vendor Name"
                                type="text"
                                name={`vendors.${vendorIndex}.nameV`}
                                className="form-control"
                              />
                              <ErrorMessage
                                name={`vendors.${vendorIndex}.nameV`}
                                component="div"
                                className="error"
                              />
                            </div>
                          </Grid>
                          <Grid item xs={2}>
                            <div className="form-group">
                              <Field
                                type="radio"
                                name={`vendors.${vendorIndex}.isMain`}
                                checked={vendor.isMain}
                                onChange={(e) => {
                                  // Ensure only one vendor can be marked as main
                                  if (e.target.checked) {
                                    setFieldValue(
                                      `vendors.${vendorIndex}.isMain`,
                                      true
                                    );
                                    values.vendors.forEach((v, index) => {
                                      if (index !== vendorIndex) {
                                        setFieldValue(
                                          `vendors.${index}.isMain`,
                                          false
                                        );
                                      }
                                    });
                                  } else {
                                    // Allow unchecking
                                    setFieldValue(
                                      `vendors.${vendorIndex}.isMain`,
                                      false
                                    );
                                  }
                                }}
                              />
                              <label htmlFor={`vendors.${vendorIndex}.isMain`}>
                                Is Main:
                              </label>
                            </div>
                          </Grid>
                        </Grid>
                        <FieldArray name={`vendors.${vendorIndex}.variants`}>
                          {({ push: pushVariant, remove: removeVariant }) => (
                            <div>
                              {vendor.variants.map((variant, variantIndex) => (
                                <div
                                  key={variantIndex}
                                  className="variant-section"
                                >
                                  <Grid
                                    container
                                    spacing={2}
                                    className="align-center"
                                  >
                                    <Grid item xs={6}>
                                      <FormControl fullWidth>
                                        <InputLabel id="demo-simple-select-label">
                                          Variant
                                        </InputLabel>
                                        <Select
                                          labelId="demo-simple-select-label"
                                          id={`vendors.${vendorIndex}.variants.${variantIndex}.variant`}
                                          name={`vendors.${vendorIndex}.variants.${variantIndex}.variant`}
                                        >
                                          <MenuItem value="xs">xs</MenuItem>
                                          <MenuItem value="sm">sm</MenuItem>
                                          <MenuItem value="l">l</MenuItem>
                                          <MenuItem value="xl">xl</MenuItem>
                                        </Select>
                                      </FormControl>
                                    </Grid>
                                    <Grid item xs={6}>
                                      <FormControl fullWidth>
                                        <InputLabel id="demo-simple-select-label">
                                          Number
                                        </InputLabel>
                                        <Select
                                          labelId="demo-simple-select-label"
                                          id={`vendors.${vendorIndex}.variants.${variantIndex}.number`}
                                          name={`vendors.${vendorIndex}.variants.${variantIndex}.number`}
                                        >
                                          <MenuItem value="20">20</MenuItem>
                                          <MenuItem value="25">25</MenuItem>
                                          <MenuItem value="30">30</MenuItem>
                                          <MenuItem value="40">40</MenuItem>
                                        </Select>
                                      </FormControl>
                                    </Grid>
                                  </Grid>
                                  {variantIndex > 0 && (
                                    <Button
                                      variant="outlined"
                                      color="error"
                                      onClick={() =>
                                        removeVariant(variantIndex)
                                      }
                                      className="remove-button"
                                    >
                                      Remove Variant
                                    </Button>
                                  )}
                                </div>
                              ))}
                              <Button
                                variant="contained"
                                onClick={() =>
                                  pushVariant({ variant: "", number: "" })
                                }
                                className="add-button"
                              >
                                Add Variant
                              </Button>
                            </div>
                          )}
                        </FieldArray>
                        {vendorIndex > 0 && (
                          <Button
                            variant="outlined"
                            color="error"
                            onClick={() => remove(vendorIndex)}
                            className="remove-button"
                          >
                            Remove Vendor
                          </Button>
                        )}
                      </div>
                    ))}
                    <Button
                      type="button"
                      variant="contained"
                      onClick={() =>
                        push({
                          vendorName: "",
                          isMain: false,
                          variants: [{ variant: "", number: "" }],
                        })
                      }
                      className="add-button"
                    >
                      Add Vendor
                    </Button>
                  </div>
                )}
              </FieldArray>

              <button type="submit" className="submit-button">
                Submit
              </button>
            </Form>
          )}
        </Formik>
      </Card>
    </div>
  );
};

export default CreateProduct;
