import React from "react";

import Card from "@mui/material/Card";
import "./CreateProduct.css";

import { Formik, Field, FieldArray, Form, ErrorMessage } from "formik";
import ProductSchema from "../../component/schema/ProductSchema";
import {
  Button,
  FormControl,
  FormControlLabel,
  Grid,
  InputLabel,
  MenuItem,
  Radio,
  RadioGroup,
  Select,
  TextField,
} from "@mui/material";

// const Item = styled(Paper)(({ theme }) => ({
//   backgroundColor: theme.palette.mode === "dark" ? "#1A2027" : "#fff",
//   ...theme.typography.body2,
//   padding: theme.spacing(1),
//   textAlign: "center",
//   color: theme.palette.text.secondary,
// }));

const CreateProduct = () => {
  const handleFormSubmit = (values) => {
    // You can dispatch an action to save the data to Redux here.
    console.log(values);
  };

  return (
    <div className="create-product">
      <Card className="varient-card">
        <Formik
          initialValues={{
            name: "",
            description: "",
            vendors: [
              {
                vendorName: "",
                isMain: true,
                variants: [{ variant: "", number: "" }],
              },
            ],
          }}
          validationSchema={ProductSchema}
          onSubmit={handleFormSubmit}
        >
          {({ values, setFieldValue }) => (
            <Form>
              <Grid container spacing={3}>
                <Grid item xs={12}>
                  <TextField
                    fullWidth
                    label="Name"
                    id="name"
                    name="name"
                    variant="outlined"
                    value={values.name}
                    onChange={(e) => setFieldValue("name", e.target.value)}
                  />
                  <ErrorMessage name="name" component="div" className="error" />
                </Grid>
                <Grid item xs={12}>
                  <TextField
                    fullWidth
                    label="Description"
                    id="description"
                    name="description"
                    variant="outlined"
                    multiline
                    rows={4}
                    value={values.description}
                    onChange={(e) =>
                      setFieldValue("description", e.target.value)
                    }
                  />
                </Grid>
                <FieldArray name="vendors">
                  {({ push, remove }) => (
                    <Grid container item xs={12} spacing={3}>
                      {values.vendors.map((vendor, vendorIndex) => (
                        <Grid item xs={12} key={vendorIndex}>
                          <Grid container item xs={12} spacing={3}>
                            <Grid item xs={4}>
                              <TextField
                                fullWidth
                                label="Vendor Name"
                                name={`vendors.${vendorIndex}.vendorName`}
                                variant="outlined"
                                value={vendor.vendorName}
                                onChange={(e) =>
                                  setFieldValue(
                                    `vendors.${vendorIndex}.vendorName`,
                                    e.target.value
                                  )
                                }
                              />
                              <ErrorMessage
                                name={`vendors.${vendorIndex}.vendorName`}
                                component="div"
                                className="error"
                              />
                            </Grid>
                            <Grid item xs={4}>
                              <FormControl fullWidth>
                                <InputLabel>Is Main</InputLabel>
                                <RadioGroup
                                  name={`vendors.${vendorIndex}.isMain`}
                                  value={vendor.isMain.toString()}
                                  onChange={(e) => {
                                    // Ensure only one vendor can be marked as main
                                    if (e.target.value === "true") {
                                      setFieldValue(
                                        `vendors.${vendorIndex}.isMain`,
                                        true
                                      );
                                      values.vendors.forEach((v, index) => {
                                        if (index !== vendorIndex) {
                                          setFieldValue(
                                            `vendors.${index}.isMain`,
                                            false
                                          );
                                        }
                                      });
                                    } else {
                                      // Allow unchecking
                                      setFieldValue(
                                        `vendors.${vendorIndex}.isMain`,
                                        false
                                      );
                                    }
                                  }}
                                >
                                  <FormControlLabel
                                    value="true"
                                    control={<Radio />}
                                    label="Yes"
                                  />
                                  <FormControlLabel
                                    value="false"
                                    control={<Radio />}
                                    label="No"
                                  />
                                </RadioGroup>
                              </FormControl>
                            </Grid>
                            <Grid item xs={12}>
                              <FieldArray
                                name={`vendors.${vendorIndex}.variants`}
                              >
                                {({
                                  push: pushVariant,
                                  remove: removeVariant,
                                }) => (
                                  <div>
                                    {vendor.variants.map(
                                      (variant, variantIndex) => (
                                        <div
                                          key={variantIndex}
                                          className="variant-section"
                                        >
                                          <h4>Variant #{variantIndex + 1}</h4>
                                          <div className="form-group">
                                            <label
                                              htmlFor={`vendors.${vendorIndex}.variants.${variantIndex}.variant`}
                                            >
                                              Variant:
                                            </label>
                                            <Field
                                              as="select"
                                              name={`vendors.${vendorIndex}.variants.${variantIndex}.variant`}
                                              className="form-control"
                                            >
                                              <option value="">
                                                Select Variant
                                              </option>
                                              <option value="Option 1">
                                                Option 1
                                              </option>
                                              <option value="Option 2">
                                                Option 2
                                              </option>
                                              {/* Add your variant options here */}
                                            </Field>
                                          </div>
                                          <div className="form-group">
                                            <label
                                              htmlFor={`vendors.${vendorIndex}.variants.${variantIndex}.number`}
                                            >
                                              Number:
                                            </label>
                                            <Field
                                              as="select"
                                              name={`vendors.${vendorIndex}.variants.${variantIndex}.number`}
                                              className="form-control"
                                            >
                                              <option value="">
                                                Select Number
                                              </option>
                                              <option value="Number 1">
                                                Number 1
                                              </option>
                                              <option value="Number 2">
                                                Number 2
                                              </option>
                                              {/* Add your number options here */}
                                            </Field>
                                          </div>
                                          {variantIndex > 0 && (
                                            <button
                                              type="button"
                                              onClick={() =>
                                                removeVariant(variantIndex)
                                              }
                                              className="remove-button"
                                            >
                                              Remove Variant
                                            </button>
                                          )}
                                        </div>
                                      )
                                    )}
                                    <button
                                      type="button"
                                      onClick={() =>
                                        pushVariant({ variant: "", number: "" })
                                      }
                                      className="add-button"
                                    >
                                      Add Variant
                                    </button>
                                  </div>
                                )}
                              </FieldArray>
                              <Grid item xs={12}>
                                <Button
                                  type="button"
                                  onClick={() =>
                                    pushVariant({ variant: "", number: "" })
                                  }
                                  variant="outlined"
                                  color="primary"
                                  className="add-button"
                                >
                                  Add Variant
                                </Button>
                              </Grid>
                              {vendorIndex > 0 && (
                                <Button
                                  type="button"
                                  onClick={() => remove(vendorIndex)}
                                  variant="outlined"
                                  color="secondary"
                                  className="remove-button"
                                >
                                  Remove Vendor
                                </Button>
                              )}
                            </Grid>
                          </Grid>
                        </Grid>
                      ))}
                    </Grid>
                  )}
                </FieldArray>
                <Grid item xs={12}>
                  <Button
                    type="submit"
                    variant="contained"
                    color="primary"
                    className="submit-button"
                  >
                    Submit
                  </Button>
                </Grid>
              </Grid>
            </Form>
          )}
        </Formik>
      </Card>
    </div>
  );
};

export default CreateProduct;
