import React from "react";
import { Drawer, List, ListItem, ListItemText, Collapse } from "@mui/material";
import { ExpandLess, ExpandMore } from "@mui/icons-material";
import "./Sidebar.css";

const Sidebar = () => {
  const [open, setOpen] = React.useState(false);

  const handleSubmenuClick = () => {
    setOpen(!open);
  };

  return (
    <Drawer variant="permanent" anchor="left" className="sidebar">
      <List>
        <ListItem button onClick={handleSubmenuClick}>
          <ListItemText primary="Parent Menu" />
          {open ? <ExpandLess /> : <ExpandMore />}
        </ListItem>
        <Collapse in={open} timeout="auto" unmountOnExit>
          <List component="div" disablePadding className="submenu">
            <ListItem button>
              <ListItemText primary="Child Menu 1" className="submenu-text" />
            </ListItem>
            <ListItem button>
              <ListItemText primary="Child Menu 2" className="submenu-text" />
            </ListItem>
          </List>
        </Collapse>
        <ListItem button>
          <ListItemText primary="Another Menu" />
        </ListItem>
      </List>
    </Drawer>
  );
};

export default Sidebar;
