import React from "react";

import Card from "@mui/material/Card";
import "./CreateProduct.css";

import { Formik, Field, FieldArray, Form, ErrorMessage } from "formik";
import ProductSchema from "../../component/schema/ProductSchema";

import { useDispatch } from "react-redux";

import { addProduct } from "../../redux/action/ProductAction";
import { useNavigate } from "react-router-dom";
import { Grid } from "@mui/material";
const CreateProduct = () => {
  const dispatch = useDispatch();
  const navigate = useNavigate();
  return (
    <div className="create-product">
      <Card className="varient-card">
        <Formik
          initialValues={{
            name: "",
            description: "",
            vendors: [
              {
                nameV: "",
                isMain: true,
                variants: [{ variant: "", number: "" }],
              },
            ],
          }}
          validationSchema={ProductSchema}
          onSubmit={(values, actions, errors) => {
            console.log(values, "values");
            dispatch(addProduct(values));
            navigate("/");
            actions.resetForm();
          }}
        >
          {({ values, setFieldValue }) => (
            <Form>
              <Grid container spacing={2}>
                <Grid item xs={6}>
                  <TextField
                    id="name"
                    name="name"
                    label="Name"
                    variant="outlined"
                    fullWidth
                    value={values.name}
                    onChange={handleChange}
                    onBlur={handleBlur}
                    errors={touched.name && Boolean(errors.name)}
                    helperText={touched.name && errors.name}
                  />
                </Grid>
                <Grid item xs={6}>
                  <div className="form-group">
                    <label htmlFor="description">Description:</label>
                    <Field
                      as="textarea"
                      id="description"
                      name="description"
                      className="form-control"
                    />
                  </div>
                </Grid>
              </Grid>
              <FieldArray name="vendors">
                {({ push, remove }) => (
                  <div>
                    {values.vendors.map((vendor, vendorIndex) => (
                      <div key={vendorIndex} className="vendor-section">
                        <div className="form-group">
                          <label htmlFor={`vendors.${vendorIndex}.nameV`}>
                            Vendor Name:
                          </label>
                          <Field
                            type="text"
                            name={`vendors.${vendorIndex}.nameV`}
                            className="form-control"
                          />
                          <ErrorMessage
                            name={`vendors.${vendorIndex}.nameV`}
                            component="div"
                            className="error"
                          />
                        </div>

                        <div className="form-group">
                          <Field
                            type="radio"
                            name={`vendors.${vendorIndex}.isMain`}
                            checked={vendor.isMain}
                            onChange={(e) => {
                              // Ensure only one vendor can be marked as main
                              if (e.target.checked) {
                                setFieldValue(
                                  `vendors.${vendorIndex}.isMain`,
                                  true
                                );
                                values.vendors.forEach((v, index) => {
                                  if (index !== vendorIndex) {
                                    setFieldValue(
                                      `vendors.${index}.isMain`,
                                      false
                                    );
                                  }
                                });
                              } else {
                                // Allow unchecking
                                setFieldValue(
                                  `vendors.${vendorIndex}.isMain`,
                                  false
                                );
                              }
                            }}
                          />
                          <label htmlFor={`vendors.${vendorIndex}.isMain`}>
                            Is Main:
                          </label>
                        </div>

                        <FieldArray name={`vendors.${vendorIndex}.variants`}>
                          {({ push: pushVariant, remove: removeVariant }) => (
                            <div>
                              {vendor.variants.map((variant, variantIndex) => (
                                <div
                                  key={variantIndex}
                                  className="variant-section"
                                >
                                  <div className="form-group">
                                    <label
                                      htmlFor={`vendors.${vendorIndex}.variants.${variantIndex}.variant`}
                                    >
                                      Variant:
                                    </label>
                                    <Field
                                      as="select"
                                      name={`vendors.${vendorIndex}.variants.${variantIndex}.variant`}
                                      className="form-control"
                                    >
                                      <option value="">Select Variant</option>
                                      <option value="xs">xs</option>
                                      <option value="sm">sm</option>
                                      <option value="l">l</option>
                                      <option value="xl">xl</option>
                                    </Field>
                                    <ErrorMessage
                                      name={`vendors.${vendorIndex}.variants.${variantIndex}.variant`}
                                      component="div"
                                      className="error"
                                    />
                                  </div>
                                  <div className="form-group">
                                    <label
                                      htmlFor={`vendors.${vendorIndex}.variants.${variantIndex}.number`}
                                    >
                                      Number:
                                    </label>
                                    <Field
                                      as="select"
                                      name={`vendors.${vendorIndex}.variants.${variantIndex}.number`}
                                      className="form-control"
                                    >
                                      <option value="">Select Number</option>
                                      <option value="20">20</option>
                                      <option value="30">30</option>
                                      <option value="25">25</option>
                                      <option value="35">35</option>
                                      {/* Add your number options here */}
                                    </Field>
                                    <ErrorMessage
                                      name={`vendors.${vendorIndex}.variants.${variantIndex}.number`}
                                      component="div"
                                      className="error"
                                    />
                                  </div>
                                  {variantIndex > 0 && (
                                    <button
                                      type="button"
                                      onClick={() =>
                                        removeVariant(variantIndex)
                                      }
                                      className="remove-button"
                                    >
                                      Remove Variant
                                    </button>
                                  )}
                                </div>
                              ))}
                              <button
                                type="button"
                                onClick={() =>
                                  pushVariant({ variant: "", number: "" })
                                }
                                className="add-button"
                              >
                                Add Variant
                              </button>
                            </div>
                          )}
                        </FieldArray>
                        {vendorIndex > 0 && (
                          <button
                            type="button"
                            onClick={() => remove(vendorIndex)}
                            className="remove-button"
                          >
                            Remove Vendor
                          </button>
                        )}
                      </div>
                    ))}
                    <button
                      type="button"
                      onClick={() =>
                        push({
                          vendorName: "",
                          isMain: false,
                          variants: [{ variant: "", number: "" }],
                        })
                      }
                      className="add-button"
                    >
                      Add Vendor
                    </button>
                  </div>
                )}
              </FieldArray>

              <button type="submit" className="submit-button">
                Submit
              </button>
            </Form>
          )}
        </Formik>
      </Card>
    </div>
  );
};

export default CreateProduct;
