import React, { useEffect, useState } from "react";
import {
  Table,
  TableHead,
  TableBody,
  TableCell,
  TableRow,
} from "@mui/material";

const Vendor = ({ product }) => {
  const [vendorVariants, setVendorVariants] = useState([]);

  useEffect(() => {
    const formattedVariants = product.vendors.map((vendor) => {
      const variants = vendor.variants
        .map((variant) =>
          Object.entries(variant)
            .map(([key, value]) => `${value}`)
            .join(" , ")
        )
        .join(" , ");
      return { vendorName: vendor.nameV, variants };
    });
    setVendorVariants(formattedVariants);
  }, [product.vendors]);

  return (
    <div className="vendor-detail">
      <Table>
        <TableHead>
          <TableRow>
            <TableCell>Vendor Name</TableCell>
            <TableCell>Variants</TableCell>
          </TableRow>
        </TableHead>
        <TableBody>
          {vendorVariants.map((vendor, index) => (
            <TableRow key={index}>
              <TableCell>{vendor.vendorName}</TableCell>
              <TableCell>{vendor.variants}</TableCell>
            </TableRow>
          ))}
        </TableBody>
      </Table>
    </div>
  );
};

export default Vendor;
