import React from "react";
import { useDispatch, useSelector } from "react-redux";
import { useNavigate } from "react-router-dom"; // Import useNavigate
import { editProduct, removeProduct } from "../../redux/action/ProductAction";
import { Button, Card, Grid } from "@mui/material";
import "./ProductCard.css";
import Vendor from "./Vendor";
const ProductList = () => {
  const products = useSelector((state) => state.products.products);
  console.log(products);
  const Navigate = useNavigate();
  const dispatch = useDispatch();
  console.log(products);

  const handleDelete = (id) => {
    dispatch(removeProduct(id));
  };

  const handleEdit = (productId) => {
    dispatch(editProduct(productId));
    console.log(productId, "dispatch");
    console.log(productId);
    Navigate(`/edit/${productId}`);
  };

  return (
    <div className="product_list">
      <Grid container spacing={2}>
        {products && products.length > 0 ? (
          products.map((product) => (
            <Grid item xs={4} key={product.id}>
              <Card
                className="product_card"

                // onClick={() => Navigate(`/edit/${product.id}`)}
              >
                <h2>Product Name : {product.name}</h2>
                <h3>Description : {product.description}</h3>
                {/* <h4>
                  Vendors:{" "}
                  {product.vendors.map((vendor) => vendor.nameV).join(" | ")}
                </h4>
                <h4>
                  Variants:{" "}
                  {product.vendors
                    .map((vendor) =>
                      vendor.variants
                        .map((variant) => {
                          const variantValues = Object.entries(variant).map(
                            ([key, value]) => `${value}`
                          );
                          return variantValues.join(" : ");
                        })
                        .join(", ")
                    )
                    .join(" | ")}
                </h4> */}
                {product.vendors.map((vendor) => (
                  <Vendor vendors={vendor} />
                ))}
                <div className="group_button">
                  <Button
                    variant="contained"
                    onClick={() => handleEdit(product.id)}
                  >
                    Edit
                  </Button>
                  <Button
                    variant="contained"
                    onClick={() => handleDelete(product.id)}
                  >
                    Delete
                  </Button>
                </div>
              </Card>
            </Grid>
          ))
        ) : (
          <Grid item xs={4}>
            <Card
              className="product_card no_data"
              // onClick={() => Navigate(`/edit/${product.id}`)}
            >
              <h3>No Product Found</h3>
              <Button variant="contained" onClick={() => Navigate("/create")}>
                Add Product
              </Button>
            </Card>
          </Grid>
        )}
      </Grid>
    </div>
  );
};

export default ProductList;
