import React, { useEffect } from "react";

import { Table } from "@mui/material";

const Vendor = ({ product }) => {
  console.log(product, "vendor component");

  const [vendor, setVendor] = React.useState(product.vendors[0]);

  useEffect(() => {
    const vendorVariants = product.vendors
      .map((vendor) => vendor.variants)
      .flat();
    const variantValues = vendorVariants
      .map((variant) =>
        Object.entries(variant).map(([key, value]) => `${value}`)
      )
      .flat()
      .join(" : ");
    setVendor(variantValues);
    console.log(variantValues + " variant values");
  }, [product.vendors]);

  console.log(vendor);
  return (
    <div className="vendor-detail">
      <Table>
        <thead>
          {product.vendors.map((vendor) => (
            <tr>
              <th>{vendor.nameV}</th>
            </tr>
          ))}
        </thead>
        <tbody>
          <tr>
            <td>{vendor}</td>
          </tr>

          <tr></tr>
        </tbody>
      </Table>
    </div>
  );
};

export default Vendor;
