import React from "react";
import DataTable from "react-data-table-component";
import { useSelector } from "react-redux";
import { useNavigate } from "react-router-dom"; // Import useNavigate

const ProductList = () => {
  const products = useSelector((state) => state.products.products);
  const Navigate = useNavigate();

  console.log(products);

  const handleEdit = (productId) => {
    console.log(productId);
    Navigate(`/edit/${productId}`);
  };

  const handleDelete = (productId) => {
    console.log(productId);
  };
  const columns = [
    {
      name: "Name",
      selector: "name",
      sortable: true,
    },
    {
      name: "vendors",
      selector: "vendors",
      cell: (row) => row.vendors.map((vendor) => vendor.nameV).join(", "),
      sortable: true,
    },
    {
      name: "variants",
      cell: (row) => {
        // Update the "variants" column definition to use a selector function
        const variantStrings = row.vendors.map((vendor) =>
          vendor.variants
            .map((variant) => Object.keys(variant).join(", "))
            .join(", ")
        );
        return variantStrings.join(", ");
      },
      sortable: true,
    },
    {
      name: "Actions",
      cell: (row) => (
        <div>
          <button onClick={() => handleEdit(row.id)}>Edit</button>
          <button onClick={() => handleDelete(row.id)}>Delete</button>
        </div>
      ),
    },
  ];

  return (
    <div>
      <DataTable
        title="Product Data"
        columns={columns}
        data={products}
        pagination
      />
    </div>
  );
};

export default ProductList;
