import React from "react";
import Card from "@mui/material/Card";
import { Formik, Form, Field, FieldArray, ErrorMessage } from "formik";
import * as Yup from "yup"; // Import Yup for validation
import { useDispatch, useSelector } from "react-redux";
import { useNavigate } from "react-router-dom"; // Import useNavigate
import { editProduct } from "../../redux/action/ProductAction";

const EditProduct = () => {
  const dispatch = useDispatch();
  const navigate = useNavigate(); // Access the navigate function
  const productId = useSelector((state) => state.products.products);
  console.log(productId, "productId");
  const product = useSelector((state) =>
    state.products.find((product) => product.id === productId)
  );

  const validationSchema = Yup.object().shape({
    name: Yup.string().required("Name is required"),
    description: Yup.string(),
    vendors: Yup.array().of(
      Yup.object().shape({
        nameV: Yup.string().required("Vendor Name is required"),
        isMain: Yup.boolean(),
        variants: Yup.array().of(
          Yup.object().shape({
            variant: Yup.string(),
            number: Yup.string(),
          })
        ),
      })
    ),
  });

  return (
    <div className="edit-product">
      <Card className="variant-card">
        <Formik
          initialValues={product}
          validationSchema={validationSchema}
          onSubmit={(values) => {
            // Handle the form submission and dispatch your update action
            dispatch(editProduct(values));
            // After updating, navigate back to the product list
            navigate("/product");
          }}
        >
          {({ values, setFieldValue }) => (
            <Form>
              <div className="form-group">
                <label htmlFor="name">Name:</label>
                <Field
                  type="text"
                  id="name"
                  name="name"
                  className="form-control"
                />
                <ErrorMessage name="name" component="div" className="error" />
              </div>

              <div className="form-group">
                <label htmlFor="description">Description:</label>
                <Field
                  as="textarea"
                  id="description"
                  name="description"
                  className="form-control"
                />
              </div>

              <FieldArray name="vendors">
                {({ push, remove }) => (
                  <div>
                    {values.vendors.map((vendor, vendorIndex) => (
                      <div key={vendorIndex} className="vendor-section">
                        <h3>Vendor #{vendorIndex + 1}</h3>
                        <div className="form-group">
                          <label htmlFor={`vendors.${vendorIndex}.nameV`}>
                            Vendor Name:
                          </label>
                          <Field
                            type="text"
                            name={`vendors.${vendorIndex}.nameV`}
                            className="form-control"
                          />
                          <ErrorMessage
                            name={`vendors.${vendorIndex}.nameV`}
                            component="div"
                            className="error"
                          />
                        </div>

                        <div className="form-group">
                          <label htmlFor={`vendors.${vendorIndex}.isMain`}>
                            Is Main:
                          </label>
                          <Field
                            type="radio"
                            name={`vendors.${vendorIndex}.isMain`}
                            checked={vendor.isMain}
                            onChange={(e) => {
                              // Ensure only one vendor can be marked as main
                              if (e.target.checked) {
                                setFieldValue(
                                  `vendors.${vendorIndex}.isMain`,
                                  true
                                );
                                values.vendors.forEach((v, index) => {
                                  if (index !== vendorIndex) {
                                    setFieldValue(
                                      `vendors.${index}.isMain`,
                                      false
                                    );
                                  }
                                });
                              } else {
                                // Allow unchecking
                                setFieldValue(
                                  `vendors.${vendorIndex}.isMain`,
                                  false
                                );
                              }
                            }}
                          />
                        </div>

                        <FieldArray name={`vendors.${vendorIndex}.variants`}>
                          {({ push: pushVariant, remove: removeVariant }) => (
                            <div>
                              {vendor.variants.map((variant, variantIndex) => (
                                <div
                                  key={variantIndex}
                                  className="variant-section"
                                >
                                  <h4>Variant #{variantIndex + 1}</h4>
                                  <div className="form-group">
                                    <label
                                      htmlFor={`vendors.${vendorIndex}.variants.${variantIndex}.variant`}
                                    >
                                      Variant:
                                    </label>
                                    <Field
                                      as="select"
                                      name={`vendors.${vendorIndex}.variants.${variantIndex}.variant`}
                                      className="form-control"
                                    >
                                      <option value="">Select Variant</option>
                                      <option value="xs">xs</option>
                                      <option value="sm">sm</option>
                                      <option value="l">l</option>
                                      <option value="xl">xl</option>
                                    </Field>
                                    <ErrorMessage
                                      name={`vendors.${vendorIndex}.variants.${variantIndex}.variant`}
                                      component="div"
                                      className="error"
                                    />
                                  </div>
                                  <div className="form-group">
                                    <label
                                      htmlFor={`vendors.${vendorIndex}.variants.${variantIndex}.number`}
                                    >
                                      Number:
                                    </label>
                                    <Field
                                      as="select"
                                      name={`vendors.${vendorIndex}.variants.${variantIndex}.number`}
                                      className="form-control"
                                    >
                                      <option value="">Select Number</option>
                                      <option value="20">20</option>
                                      <option value="30">30</option>
                                      <option value="25">25</option>
                                      <option value="35">35</option>
                                      {/* Add your number options here */}
                                    </Field>
                                    <ErrorMessage
                                      name={`vendors.${vendorIndex}.variants.${variantIndex}.number`}
                                      component="div"
                                      className="error"
                                    />
                                  </div>
                                  {variantIndex > 0 && (
                                    <button
                                      type="button"
                                      onClick={() =>
                                        removeVariant(variantIndex)
                                      }
                                      className="remove-button"
                                    >
                                      Remove Variant
                                    </button>
                                  )}
                                </div>
                              ))}
                              <button
                                type="button"
                                onClick={() =>
                                  pushVariant({ variant: "", number: "" })
                                }
                                className="add-button"
                              >
                                Add Variant
                              </button>
                            </div>
                          )}
                        </FieldArray>
                        {vendorIndex > 0 && (
                          <button
                            type="button"
                            onClick={() => remove(vendorIndex)}
                            className="remove-button"
                          >
                            Remove Vendor
                          </button>
                        )}
                      </div>
                    ))}
                    <button
                      type="button"
                      onClick={() =>
                        push({
                          vendorName: "",
                          isMain: false,
                          variants: [{ variant: "", number: "" }],
                        })
                      }
                      className="add-button"
                    >
                      Add Vendor
                    </button>
                  </div>
                )}
              </FieldArray>

              <button type="submit" className="submit-button">
                Submit
              </button>
            </Form>
          )}
        </Formik>
      </Card>
    </div>
  );
};

export default EditProduct;
