import React, { useEffect } from "react";
import Variants from "./Variants";
import { Table } from "@mui/material";

const Vendor = ({ product }) => {
  console.log(product, "vendor component");

  const [vendor, setVendor] = React.useState(product.vendors[0]);

  useEffect((product) => {
    {
      product.vendors
        .map((vendor) =>
          vendor.variants
            .map((variant) => {
              const variantValues = Object.entries(variant).map(
                ([key, value]) => `${value}`
              );
              setVendor(variantValues.join(" : "));
            })
            .join(", ")
        )
        .join(" | ");
    }
  });
  return (
    <div className="vendor-detail">
      <Table>
        <thead>
          {product.vendors.map((vendor) => (
            <tr>
              <th>{vendor.nameV}</th>
            </tr>
          ))}
        </thead>
        <tbody>
          <tr>
            <td>{vendor?.name}</td>
          </tr>
        </tbody>
      </Table>
    </div>
  );
};

export default Vendor;
