import React from "react";

import Card from "@mui/material/Card";
import "./CreateProduct.css";

import { Formik, Field, FieldArray, Form, ErrorMessage } from "formik";
import ProductSchema from "../../component/schema/ProductSchema";
import {
  Button,
  FormControl,
  FormControlLabel,
  Grid,
  InputLabel,
  MenuItem,
  RadioGroup,
  Select,
  TextField,
} from "@mui/material";

// const Item = styled(Paper)(({ theme }) => ({
//   backgroundColor: theme.palette.mode === "dark" ? "#1A2027" : "#fff",
//   ...theme.typography.body2,
//   padding: theme.spacing(1),
//   textAlign: "center",
//   color: theme.palette.text.secondary,
// }));

const CreateProduct = () => {
  const handleFormSubmit = (values) => {
    // You can dispatch an action to save the data to Redux here.
    console.log(values);
  };

  return (
    <div className="create-product">
      <Card className="varient-card">
        <Formik
          initialValues={{
            name: "",
            description: "",
            vendors: [
              {
                vendorName: "",
                isMain: true,
                variants: [{ variant: "", number: "" }],
              },
            ],
          }}
          validationSchema={ProductSchema}
          onSubmit={handleFormSubmit}
        >
          {({ values, setFieldValue }) => (
            <Form>
              <Grid container spacing={3}>
                <Grid item xs={12}>
                  <TextField
                    fullWidth
                    label="Name"
                    id="name"
                    name="name"
                    variant="outlined"
                    value={values.name}
                    onChange={(e) => setFieldValue("name", e.target.value)}
                  />
                  <ErrorMessage name="name" component="div" className="error" />
                </Grid>
                <Grid item xs={12}>
                  <TextField
                    fullWidth
                    label="Description"
                    id="description"
                    name="description"
                    variant="outlined"
                    multiline
                    rows={4}
                    value={values.description}
                    onChange={(e) =>
                      setFieldValue("description", e.target.value)
                    }
                  />
                </Grid>
                <FieldArray name="vendors">
                  {({ push, remove }) => (
                    <Grid container item xs={12} spacing={3}>
                      {values.vendors.map((vendor, vendorIndex) => (
                        <Grid item xs={12} key={vendorIndex}>
                          <Grid container item xs={12} spacing={3}>
                            <Grid item xs={4}>
                              <TextField
                                fullWidth
                                label="Vendor Name"
                                name={`vendors.${vendorIndex}.vendorName`}
                                variant="outlined"
                                value={vendor.vendorName}
                                onChange={(e) =>
                                  setFieldValue(
                                    `vendors.${vendorIndex}.vendorName`,
                                    e.target.value
                                  )
                                }
                              />
                              <ErrorMessage
                                name={`vendors.${vendorIndex}.vendorName`}
                                component="div"
                                className="error"
                              />
                            </Grid>
                            <Grid item xs={4}>
                              <FormControl fullWidth>
                                <InputLabel>Is Main</InputLabel>
                                <RadioGroup
                                  name={`vendors.${vendorIndex}.isMain`}
                                  value={vendor.isMain.toString()}
                                  onChange={(e) => {
                                    // Ensure only one vendor can be marked as main
                                    if (e.target.value === "true") {
                                      setFieldValue(
                                        `vendors.${vendorIndex}.isMain`,
                                        true
                                      );
                                      values.vendors.forEach((v, index) => {
                                        if (index !== vendorIndex) {
                                          setFieldValue(
                                            `vendors.${index}.isMain`,
                                            false
                                          );
                                        }
                                      });
                                    } else {
                                      // Allow unchecking
                                      setFieldValue(
                                        `vendors.${vendorIndex}.isMain`,
                                        false
                                      );
                                    }
                                  }}
                                >
                                  <FormControlLabel
                                    value="true"
                                    control={<Radio />}
                                    label="Yes"
                                  />
                                  <FormControlLabel
                                    value="false"
                                    control={<Radio />}
                                    label="No"
                                  />
                                </RadioGroup>
                              </FormControl>
                            </Grid>
                            <Grid item xs={12}>
                              <FieldArray
                                name={`vendors.${vendorIndex}.variants`}
                              >
                                {({
                                  push: pushVariant,
                                  remove: removeVariant,
                                }) => (
                                  <Grid container spacing={3}>
                                    {vendor.variants.map(
                                      (variant, variantIndex) => (
                                        <>
                                          <Grid item xs={4} key={variantIndex}>
                                            <TextField
                                              fullWidth
                                              label="Variant"
                                              name={`vendors.${vendorIndex}.variants.${variantIndex}.variant`}
                                              variant="outlined"
                                              value={variant.variant}
                                              onChange={(e) =>
                                                setFieldValue(
                                                  `vendors.${vendorIndex}.variants.${variantIndex}.variant`,
                                                  e.target.value
                                                )
                                              }
                                            />
                                            <ErrorMessage
                                              name={`vendors.${vendorIndex}.variants.${variantIndex}.variant`}
                                              component="div"
                                              className="error"
                                            />
                                          </Grid>
                                          <Grid item xs={4} key={variantIndex}>
                                            <FormControl fullWidth>
                                              <InputLabel>Number</InputLabel>
                                              <Select
                                                name={`vendors.${vendorIndex}.variants.${variantIndex}.number`}
                                                variant="outlined"
                                                value={variant.number}
                                                onChange={(e) =>
                                                  setFieldValue(
                                                    `vendors.${vendorIndex}.variants.${variantIndex}.number`,
                                                    e.target.value
                                                  )
                                                }
                                              >
                                                <MenuItem value="">
                                                  Select Number
                                                </MenuItem>
                                                <MenuItem value="Number 1">
                                                  Number 1
                                                </MenuItem>
                                                <MenuItem value="Number 2">
                                                  Number 2
                                                </MenuItem>
                                                {/* Add your number options here */}
                                              </Select>
                                            </FormControl>
                                          </Grid>
                                        </>
                                      )
                                    )}
                                  </Grid>
                                )}
                              </FieldArray>
                              {vendorIndex > 0 && (
                                <Button
                                  type="button"
                                  onClick={() => remove(vendorIndex)}
                                  variant="outlined"
                                  color="secondary"
                                  className="remove-button"
                                >
                                  Remove Vendor
                                </Button>
                              )}
                              <Button
                                type="button"
                                onClick={() =>
                                  push({
                                    vendorName: "",
                                    isMain: false,
                                    variants: [{ variant: "", number: "" }],
                                  })
                                }
                                variant="outlined"
                                color="primary"
                                className="add-button"
                              >
                                Add Vendor
                              </Button>
                            </Grid>
                          </Grid>
                        </Grid>
                      ))}
                    </Grid>
                  )}
                </FieldArray>
                <Grid item xs={12}>
                  <Button
                    type="submit"
                    variant="contained"
                    color="primary"
                    className="submit-button"
                  >
                    Submit
                  </Button>
                </Grid>
              </Grid>
            </Form>
          )}
        </Formik>
      </Card>
    </div>
  );
};

export default CreateProduct;
